mapper.select:
SELECT `tk_client_info`.tk_client_id, `tk_client_info`.tk_client_name, `tk_client_info`.tk_client_phone, `tk_client_info`.tk_client_location, `tk_client_info`.tk_client_fresh_date FROM `tk_client_info` where 1
and tk_client_type_id=#{tk_client_type_id,jdbcType=VARCHAR}
ORDER BY tk_client_joindate desc;

mapper.update:
update tk_client_info
set tk_client_type_id =
#{tk_client_type_id,jdbcType=VARCHAR}
where tk_client_id =
#{tk_client_id ,jdbcType=VARCHAR}

mapper.select:
SELECT * from (SELECT count(*) as num ,tk_channel_id FROM (SELECT * from `tk_client_info` WHERE tk_client_user_id= #{tkUserId}
and tk_client_joindate > date(#{startTime})
and tk_client_joindate < date(#{endTime})
) as temp where 1 GROUP BY tk_channel_id) as a,tk_channel as b where a.tk_channel_id=b.tk_channel_id

mapper.update:
update tk_client_info set tk_client_fresh_date=#{tk_client_fresh_date}
,tk_client_type_id=#{tk_client_fresh_date}

,tk_client_user_id=#{tk_client_user_id}

where 1

	and tk_client_id=#{tk_client_id}

;
